Â  Â  { id: 'test', name: 'Usar Modelo', icon: FiPlay, color: 'from-blue-500 to-indigo-500' },
Â  Â  { id: 'learn', name: 'Aprender SeÃ±as', icon: FiBook, color: 'from-indigo-500 to-purple-500' },
Â  Â  { id: 'calculate', name: 'Calculadora', icon: FiCpu, color: 'from-green-500 to-lime-500' },
Â  Â  { id: 'import', name: 'Importar', icon: FiDownload, color: 'from-purple-500 to-pink-500' },
Â  Â  { id: 'visualize', name: 'Analytics', icon: FiBarChart, color: 'from-cyan-500 to-blue-500' },
Â  Â  { id: 'settings', name: 'ConfiguraciÃ³n', icon: FiSettings, color: 'from-gray-500 to-gray-600' }
Â  ];

Â  useEffect(() => {
Â  Â  loadModels();
Â  Â  initializeSettings();
Â  }, []);

Â  const initializeSettings = () => {
Â  Â  const savedTheme = localStorage.getItem('theme') as 'light' | 'dark';
Â  Â  if (savedTheme) {
Â  Â  Â  setTheme(savedTheme);
Â  Â  }

Â  Â  const unsubscribe = settingsManager.subscribe((newSettings) => {
Â  Â  Â  setSettings(newSettings);
Â  Â  Â  applyGlobalSettings(newSettings);
Â  Â  });

Â  Â  const currentSettings = settingsManager.export();
Â  Â  if (currentSettings !== '{}') {
Â  Â  Â  setSettings(JSON.parse(currentSettings));
Â  Â  Â  applyGlobalSettings(JSON.parse(currentSettings));
Â  Â  }

Â  Â  return unsubscribe;
Â  };

Â  const applyGlobalSettings = (settings: any) => {
Â  Â  if (settings.appearance) {
Â  Â  Â  const body = document.body;
Â  Â  Â  body.classList.remove('high-contrast', 'compact-mode', 'no-animations');

Â  Â  Â  if (settings.appearance.highContrast) {
Â  Â  Â  Â  body.classList.add('high-contrast');
Â  Â  Â  }

Â  Â  Â  if (settings.appearance.compactMode) {
Â  Â  Â  Â  body.classList.add('compact-mode');
Â  Â  Â  }

Â  Â  Â  if (!settings.appearance.animations) {
Â  Â  Â  Â  body.classList.add('no-animations');
Â  Â  Â  }

Â  Â  Â  if (settings.appearance.fontSize) {
Â  Â  Â  Â  document.documentElement.style.fontSize = getFontSizeValue(settings.appearance.fontSize);
Â  Â  Â  }

Â  Â  Â  if (settings.appearance.borderRadius) {
Â  Â  Â  Â  document.documentElement.style.setProperty('--border-radius-base', getBorderRadiusValue(settings.appearance.borderRadius));
Â  Â  Â  }
Â  Â  }

Â  Â  if (settings.advanced?.debugMode) {
Â  Â  Â  window.signflowDebug = true;
Â  Â  Â  console.log('Debug mode enabled globally');
Â  Â  } else {
Â  Â  Â  window.signflowDebug = false;
Â  Â  }
Â  };

Â  const getFontSizeValue = (size: string) => {
Â  Â  const sizes = {
Â  Â  Â  'small': '14px',
Â  Â  Â  'medium': '16px',
Â  Â  Â  'large': '18px',
Â  Â  Â  'extra-large': '20px'
Â  Â  };
Â  Â  return sizes[size as keyof typeof sizes] || sizes.medium;
Â  };

Â  const getBorderRadiusValue = (radius: string) => {
Â  Â  const radiuses = {
Â  Â  Â  'none': '0px',
Â  Â  Â  'small': '4px',
Â  Â  Â  'normal': '8px',
Â  Â  Â  'large': '16px'
Â  Â  };
Â  Â  return radiuses[radius as keyof typeof radiuses] || radiuses.normal;
Â  };

