Â  Â  Â  Â  default:
Â  Â  Â  Â  Â  return <div><ModelCreator onModelCreated={handleModelCreated} /></div>;
Â  Â  Â  }
Â  Â  }
Â  };

Â  if (isLoading) {
Â  Â  const shouldAnimate = settings.appearance?.animations !== false;
Â  Â  return (
Â  Â  Â  <div className={`min-h-screen flex items-center justify-center ${
Â  Â  Â  Â  theme === 'dark' ? 'bg-gray-900' : 'bg-gray-50'
Â  Â  Â  }`}>
