import React, { useState, useRef, useEffect, useCallback } from "react";
import { motion } from "framer-motion";
import {
  FiCamera,
  FiCameraOff,
  FiXCircle,
  FiCheckCircle,
} from "react-icons/fi";
import toast from "react-hot-toast";
import { MediaPipeHandler, createMediaPipeHandler } from "../utils/mediapipe";
import { Results } from "@mediapipe/hands";
import { api } from "../utils/api";

const SignLanguageCalculator: React.FC = () => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const mediaPipeHandler = useRef<MediaPipeHandler | null>(null);

  const [cameraActive, setCameraActive] = useState(false);
  const [isInitializing, setIsInitializing] = useState(false);
  const [handsDetected, setHandsDetected] = useState(false);
  const [expression, setExpression] = useState<string>("");
  const [result, setResult] = useState<string>("");

  const currentLandmarksRef = useRef<any[] | null>(null);

  const onResults = useCallback((results: Results): void => {
    const hasHands =
      results.multiHandLandmarks && results.multiHandLandmarks.length > 0;
    setHandsDetected(hasHands);

    if (hasHands) {
      const landmarks = results.multiHandLandmarks[0];
      if (landmarks && landmarks.length === 21) {
        currentLandmarksRef.current = landmarks;
      }
    } else {
      currentLandmarksRef.current = null;
    }
  }, []);

  const startCamera = async () => {
    if (!videoRef.current || !canvasRef.current) {
      toast.error("Error: No se encontró el video/canvas");
      return;
    }
    setIsInitializing(true);
    try {
      if (mediaPipeHandler.current) {
        mediaPipeHandler.current.stop();
      }

      mediaPipeHandler.current = createMediaPipeHandler();
      const success = await mediaPipeHandler.current.initialize(
        videoRef.current,
        canvasRef.current,
        onResults
      );

      if (success) {
        setCameraActive(true);
        toast.success("Cámara iniciada");
      }
    } catch (err) {
      console.error(err);
      toast.error("Error al iniciar la cámara");
    } finally {
      setIsInitializing(false);
    }
  };

  const stopCamera = () => {
    if (mediaPipeHandler.current) {
      mediaPipeHandler.current.stop();
      setCameraActive(false);
      setHandsDetected(false);
      currentLandmarksRef.current = null;
      toast.success("Cámara detenida");
    }
  };

  const recognizeSign = async () => {
    if (!currentLandmarksRef.current) {
      toast.error("No se detecta la mano");
      return;
    }
    try {
      const response = await api.post("/prediction/predict", {
        modelId: "calculate",
        landmarks: currentLandmarksRef.current.map((l: any) => ({
          x: l.x,
          y: l.y,
          z: l.z,
        })),
      });

      const { prediction, confidence } = response.data;

      if (confidence < 0.7) {
        toast.error("Seña no clara, intenta de nuevo");
        return;
      }

      if (["+", "-", "*", "/"].includes(prediction)) {
        setExpression((prev) => prev + ` ${prediction} `);
      } else if (!isNaN(Number(prediction))) {
        setExpression((prev) => prev + prediction);
      } else if (prediction === "clear") {
        setExpression("");
        setResult("");
      } else if (prediction === "equal") {
        try {
          // eslint-disable-next-line no-eval
          const evalResult = eval(expression);
          setResult(evalResult.toString());
        } catch {
          toast.error("Expresión inválida");
        }
      }
    } catch (err) {
      console.error("Error predicting sign:", err);
      toast.error("Error al reconocer seña");
    }
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
      {/* Sidebar */}
      <aside className="md:col-span-1 space-y-6">
        <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-4">
          <h3 className="font-bold text-lg mb-2">Seleccionar Modelo</h3>
          <select className="w-full p-2 rounded-lg border dark:bg-gray-700 dark:border-gray-600">
            <option>Operaciones Matemáticas</option>
            <option>Abecedario</option>
          </select>
          <button
            onClick={cameraActive ? stopCamera : startCamera}
            disabled={isInitializing}
            className={`mt-4 w-full px-4 py-2 rounded-lg font-bold text-white ${
              cameraActive ? "bg-red-500" : "bg-blue-500"
            }`}
          >
            {cameraActive ? "Detener Cámara" : "Iniciar Cámara"}
          </button>
        </div>

        <div className="bg-white dark:bg-gray-800 rounded-xl shadow p-4">
          <h3 className="font-bold text-lg mb-2">Estado</h3>
          {handsDetected ? (
            <p className="flex items-center gap-2 text-green-600">
              <FiCheckCircle /> Mano detectada
            </p>
          ) : (
            <p className="flex items-center gap-2 text-red-600">
              <FiXCircle /> No hay mano
            </p>
          )}
        </div>
      </aside>

      {/* Main Area */}
      <section className="md:col-span-3 space-y-6">
        <motion.h2
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          className="text-3xl font-bold text-center"
        >
          Calculadora con Señas ✋➕✌️
        </motion.h2>

        <div className="relative bg-black rounded-xl overflow-hidden shadow-lg mx-auto max-w-2xl">
          <video ref={videoRef} className="hidden" autoPlay muted playsInline />
          <canvas
            ref={canvasRef}
            width={640}
            height={480}
            className="w-full"
          />

          {!cameraActive && (
            <div className="absolute inset-0 flex items-center justify-center bg-gray-800 text-white">
              <p>Activa la cámara para empezar</p>
            </div>
          )}
        </div>

        <div className="flex justify-center gap-4">
          <motion.button
            whileHover={{ scale: 1.05 }}
            onClick={recognizeSign}
            disabled={!cameraActive}
            className="px-4 py-2 rounded-lg bg-green-500 text-white font-bold disabled:opacity-50"
          >
            Reconocer Seña
          </motion.button>
        </div>

        <div className="max-w-lg mx-auto bg-white dark:bg-gray-800 rounded-xl shadow-md p-6 text-center space-y-3">
          <div className="text-xl font-mono">{expression || "0"}</div>
          {result && (
            <div className="text-2xl font-bold text-green-600">= {result}</div>
          )}
        </div>
      </section>
    </div>
  );
};

export default SignLanguageCalculator;
