import React, { useState, useCallback, useEffect } from 'react';
import { FiCpu, FiRefreshCw, FiTarget } from 'react-icons/fi'; // Iconos para la UI
import toast from 'react-hot-toast'; // Usamos toast para notificaciones consistentes
import { motion } from 'framer-motion'; // Usamos motion para seguir el estilo de la app

// ‚ö†Ô∏è NOTA IMPORTANTE: Esta es una funci√≥n SIMULADA.
// En una aplicaci√≥n real, esta funci√≥n debe comunicarse con tu API de Node.js
// (ej: fetch('/api/detect-sign-gesture', ...)) para recibir la detecci√≥n.
// Asume que la API devuelve: { type: 'number', value: 5 } o { type: 'operator', value: '+' }
const simulateSignDetection = async (instruction: string): Promise<any> => {
  console.log(`Esperando gesto para: ${instruction}...`);
  
  // Simulaci√≥n de latencia de red/procesamiento
  await new Promise(resolve => setTimeout(resolve, 1500 + Math.random() * 500));
  
  // L√≥gica de simulaci√≥n para devolver el tipo de dato esperado
  if (instruction.includes("primer n√∫mero") || instruction.includes("segundo n√∫mero")) {
      // Simula el gesto de un n√∫mero (1 al 9)
      const detectedValue = Math.floor(Math.random() * 9) + 1;
      return { type: 'number', value: detectedValue }; 
  } else if (instruction.includes("operaci√≥n")) {
      // Simula el gesto de una operaci√≥n (ej: '+', '-', '*', '/')
      const operators = ['+', '-', '*', '/'];
      const detectedOperator = operators[Math.floor(Math.random() * operators.length)];
      return { type: 'operator', value: detectedOperator };
  }
  // Valor de fallback en caso de error de detecci√≥n
  throw new Error("No se detect√≥ un gesto v√°lido. Intente de nuevo.");
};


// ----------------------------------------------------------------------
// L√≥gica y Componente Principal
// ----------------------------------------------------------------------

type CalculatorState = 'waitingForNum1' | 'waitingForOperator' | 'waitingForNum2' | 'calculating' | 'result' | 'error';

interface SignLanguageCalculatorProps {
    // Puedes pasar props como el tema si es necesario, pero lo haremos simple por ahora.
}

const SignLanguageCalculator: React.FC<SignLanguageCalculatorProps> = () => {
  const [num1, setNum1] = useState<number | null>(null);
  const [operator, setOperator] = useState<string | null>(null);
  const [num2, setNum2] = useState<number | null>(null);
  const [result, setResult] = useState<number | string | null>(null);
  const [state, setState] = useState<CalculatorState>('waitingForNum1');
  const [message, setMessage] = useState<string>("Muestre el primer n√∫mero en Lenguaje de Se√±as.");
  const [isDetecting, setIsDetecting] = useState<boolean>(false);
  
  // Asumimos que el tema se gestiona globalmente, pero para la UI b√°sica
  const theme = localStorage.getItem('theme') as 'light' | 'dark' || 'dark';


  // Funci√≥n para realizar el c√°lculo basado en los tres valores
  const calculateResult = useCallback((n1: number, op: string, n2: number): number | string => {
    switch (op) {
      case '+': return n1 + n2;
      case '-': return n1 - n2;
      case '*': return n1 * n2;
      case '/': 
        if (n2 === 0) return "Divisi√≥n por cero no permitida.";
        return parseFloat((n1 / n2).toFixed(2)); // Limitar decimales
      default: return "Error de operador.";
    }
  }, []);

  // Funci√≥n principal para manejar la detecci√≥n de gestos y avanzar el estado
  const processGesture = useCallback(async () => {
    if (isDetecting || state === 'calculating' || state === 'result') return;
    
    setIsDetecting(true);
    setMessage(`üëÄ Esperando y procesando su gesto...`);

    let gestureResult: any;
    try {
        gestureResult = await simulateSignDetection(message); 
        
    } catch (err: any) {
        toast.error(`Error de Detecci√≥n: ${err.message || "Gesto no reconocido."}`);
        setState('error');
        setIsDetecting(false);
        setMessage("‚ö†Ô∏è Intente de nuevo. Reinicie o muestre el gesto claramente.");
        return;
    }

    // L√≥gica para actualizar el estado de la calculadora
    const value = gestureResult.value;
    
    if (gestureResult.type === 'number' && (state === 'waitingForNum1' || state === 'waitingForNum2')) {
        if (state === 'waitingForNum1') {
            setNum1(value);
            setState('waitingForOperator');
            toast.success(`Primer n√∫mero detectado: ${value}`);
            setMessage(`‚úÖ N√∫mero 1: ${value}. Ahora muestre la **operaci√≥n** (+, -, *, /).`);
        } else { // waitingForNum2
            setNum2(value);
            setState('calculating');
            toast.success(`Segundo n√∫mero detectado: ${value}`);
            setMessage(`‚úÖ N√∫mero 2: ${value}. Calculando...`);
        }
    } else if (gestureResult.type === 'operator' && state === 'waitingForOperator') {
        setOperator(value);
        setState('waitingForNum2');
        toast.success(`Operaci√≥n detectada: ${value}`);
        setMessage(`‚úÖ Operaci√≥n: ${value}. Ahora muestre el **segundo n√∫mero**.`);
    } else {
        // Error de tipo inesperado (ej: n√∫mero cuando se espera operador)
        toast.error(`Se esperaba un ${state.includes('Num') ? 'n√∫mero' : 'operador'}.`);
        setMessage("‚ö†Ô∏è Gesto incorrecto. Vuelva a intentar el paso actual.");
    }
    
    setIsDetecting(false);

  }, [state, message, isDetecting]);


  // Effect para disparar el c√°lculo final y el mensaje de estado
  useEffect(() => {
    if (state === 'calculating' && num1 !== null && operator !== null && num2 !== null) {
      const finalResult = calculateResult(num1, operator, num2);
      setResult(finalResult);
      setState('result');
      if (typeof finalResult === 'number') {
        toast.success(`Resultado: ${finalResult}`, { duration: 5000 });
      } else {
        toast.error(`${finalResult}`);
      }
    }
  }, [state, num1, operator, num2, calculateResult]);


  // Funci√≥n para reiniciar la calculadora
  const resetCalculator = () => {
    setNum1(null);
    setOperator(null);
    setNum2(null);
    setResult(null);
    setIsDetecting(false);
    setState('waitingForNum1');
    setMessage("Muestre el primer n√∫mero en Lenguaje de Se√±as.");
    toast('Calculadora Reiniciada', { icon: 'üîÑ' });
  };

  // Determinar si el bot√≥n de detecci√≥n debe estar habilitado
  const isDetectionButtonDisabled = isDetecting || state === 'calculating' || state === 'result';


  // ----------------------------------------------------------------------
  // Renderizado de la UI
  // ----------------------------------------------------------------------
  return (
    <div className={`p-6 rounded-xl transition-colors duration-300 ${
        theme === 'dark' 
            ? 'bg-gray-800 text-gray-100' 
            : 'bg-white text-gray-800'
    }`}>
      <motion.h2 
        initial={{ opacity: 0, y: -10 }} 
        animate={{ opacity: 1, y: 0 }} 
        className="text-3xl font-bold mb-6 flex items-center gap-3"
      >
        <FiCpu className="text-pink-500" /> Calculadora de Se√±as
      </motion.h2>

      <div className={`p-4 rounded-lg mb-6 shadow-md ${
          theme === 'dark' ? 'bg-gray-700/50' : 'bg-gray-100'
      }`}>
        <p className="text-lg font-semibold mb-2">Instrucci√≥n:</p>
        <motion.p 
            key={message}
            initial={{ opacity: 0, x: -10 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            className={`font-mono ${state.startsWith('waiting') ? 'text-yellow-400' : 'text-green-400'} text-xl`}
            dangerouslySetInnerHTML={{ __html: message.replace(/\*\*([^*]+)\*\*/g, '<strong>$1</strong>') }}
        />
      </div>

      {/* √Årea de Detecci√≥n (Simulaci√≥n de la C√°mara) */}
      <div className={`h-64 w-full flex flex-col justify-center items-center mb-6 rounded-lg border-4 border-dashed ${
          isDetecting 
            ? 'border-yellow-500 bg-yellow-900/10 animate-pulse'
            : theme === 'dark' 
            ? 'border-gray-600 bg-gray-900/30' 
            : 'border-gray-300 bg-gray-50'
      }`}>
        <FiTarget className={`text-4xl mb-2 ${isDetecting ? 'text-yellow-500' : 'text-gray-500'}`} />
        <p className={`text-center ${isDetecting ? 'text-yellow-300' : 'text-gray-400'}`}>
          [√Årea de Detecci√≥n en Vivo de la C√°mara]
        </p>
        {isDetecting && <p className="mt-2 text-sm text-yellow-300">Esperando se√±al de tu backend Node.js...</p>}
      </div>

      {/* Visualizaci√≥n de la F√≥rmula */}
      <div className={`text-center mb-6 p-4 rounded-xl shadow-lg border ${
          theme === 'dark' ? 'bg-indigo-900/50 border-indigo-700' : 'bg-indigo-100 border-indigo-300'
      }`}>
        <p className="text-3xl font-extrabold tracking-widest">
            <span className="text-blue-400">{num1 !== null ? num1 : '_'}</span>
            <span className="mx-3 text-red-400">{operator !== null ? operator : '?'}</span>
            <span className="text-blue-400">{num2 !== null ? num2 : '_'}</span>
            <span className="mx-3 text-white">=</span>
            <span className={`text-5xl font-extrabold ${state === 'result' ? 'text-pink-400' : 'text-gray-500'}`}>
                {result !== null ? result : '?'}
            </span>
        </p>
      </div>

      {/* Controles */}
      <div className="flex justify-center gap-4">
        <motion.button 
          onClick={resetCalculator} 
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          className="flex items-center gap-2 px-6 py-3 rounded-full font-semibold text-white bg-red-600 hover:bg-red-700 transition-colors"
        >
          <FiRefreshCw /> Reiniciar
        </motion.button>
        
        <motion.button 
          onClick={processGesture} 
          disabled={isDetectionButtonDisabled}
          whileHover={!isDetectionButtonDisabled ? { scale: 1.05 } : {}}
          whileTap={!isDetectionButtonDisabled ? { scale: 0.95 } : {}}
          className={`flex items-center gap-2 px-6 py-3 rounded-full font-semibold transition-colors shadow-xl ${
            isDetectionButtonDisabled 
              ? 'bg-gray-500 cursor-not-allowed text-gray-300' 
              : 'bg-green-600 hover:bg-green-700 text-white'
          }`}
        >
          {isDetecting ? 'Detectando...' : 'Simular Detecci√≥n de Gesto'}
        </motion.button>
      </div>

    </div>
  );
};

export default SignLanguageCalculator;
