import React, { useState, useRef, useEffect, useCallback, useMemo } from "react";
import { motion } from "framer-motion";
import {
  XCircle,
  CheckCircle,
  RefreshCw,
  Power,
  Zap,
  Equal,
} from "lucide-react"; 
import toast from "react-hot-toast";

// =========================================================================
//                           SIMULACIN DE MEDIAPIPE Y API
// =========================================================================

// Tipado simulado para los resultados de MediaPipe
interface Landmark { x: number; y: number; z: number; visibility?: number; }
interface HandResult { label: 'Left' | 'Right'; score: number; }

interface Results {
    // ESTE era el punto de error: puede ser 'null' si no se detectan manos.
    multiHandLandmarks: Landmark[][] | null; 
    multiHandedness: HandResult[] | null;
    image: HTMLCanvasElement;
}

// SIMULACIN DE LA API de Predicci贸n (en un entorno real, esto ser铆a una llamada HTTP)
const api = {
  post: async (path: string, data: any) => {
    // Simulaci贸n de las predicciones que har铆a un modelo real (n煤meros y operadores)
    const mockPredictions = ["1", "2", "+", "3", "*", "4", "5", "-", "6", "7", "8", "/", "9", "0", "clear"];
    const randomPrediction = mockPredictions[Math.floor(Math.random() * mockPredictions.length)];
    await new Promise(resolve => setTimeout(resolve, 150)); // Simula latencia
    return { 
        data: { 
            prediction: randomPrediction, 
            confidence: Math.random() * 0.3 + 0.7 // Confianza m铆nima 70%
        }
    };
  }
};

// SIMULACIN DEL MANEJADOR DE MEDIA PIPE (C谩mara y Detecci贸n)
interface MediaPipeHandler {
    initialize: (videoElement: HTMLVideoElement, canvasElement: HTMLCanvasElement, onResults: (results: Results) => void) => Promise<boolean>;
    stop: () => void;
}

const createMediaPipeHandler = (): MediaPipeHandler => {
    let stream: MediaStream | null = null;
    let animationFrameId: number | null = null;
    let videoElement: HTMLVideoElement | null = null;
    let canvasElement: HTMLCanvasElement | null = null;
    let onResultsCallback: ((results: Results) => void) | null = null;

    const runDetectionLoop = () => {
        if (!videoElement || !canvasElement || !onResultsCallback) {
            if (animationFrameId !== null) cancelAnimationFrame(animationFrameId);
            return;
        }

        const canvasCtx = canvasElement.getContext('2d');
        if (!canvasCtx) return;

        canvasCtx.save();
        canvasCtx.clearRect(0, 0, canvasElement.width, canvasElement.height);

        // Simula la captura de video
        if (videoElement.readyState === videoElement.HAVE_ENOUGH_DATA) {
            canvasCtx.drawImage(videoElement, 0, 0, canvasElement.width, canvasElement.height);
        } else {
             // Si el video no est谩 cargado o detenido, muestra un fondo
            canvasCtx.fillStyle = 'black';
            canvasCtx.fillRect(0, 0, canvasElement.width, canvasElement.height);
        }
        
        // L贸gica de simulaci贸n de detecci贸n de manos
        const shouldSimulateHands = stream && Math.random() > 0.1; // 90% de probabilidad de tener manos
        const mockResults: Results = {
            multiHandLandmarks: shouldSimulateHands ? [[ 
                // Simulaci贸n de 21 landmarks (puntos de la mano)
                Array.from({ length: 21 }, (_, i) => ({ x: Math.random(), y: Math.random(), z: Math.random() })) as Landmark[] 
            ]] : null, // Puede ser NULL, activando la correcci贸n de TS
            image: canvasElement,
            multiHandedness: shouldSimulateHands ? [{ label: 'Right', score: 0.9 }] : null,
        } as unknown as Results; 

        onResultsCallback(mockResults);

        canvasCtx.restore();
        animationFrameId = requestAnimationFrame(runDetectionLoop);
    };

    const initialize = async (video: HTMLVideoElement, canvas: HTMLCanvasElement, onResults: (results: Results) => void) => {
        videoElement = video;
        canvasElement = canvas;
        onResultsCallback = onResults;

        try {
            stream = await navigator.mediaDevices.getUserMedia({ video: true });
            video.srcObject = stream;
            
            await new Promise((resolve) => {
                video.onloadedmetadata = () => {
                    video.play();
                    resolve(true);
                };
            });

            runDetectionLoop();
            return true;
        } catch (error) {
            console.error("Error al acceder a la c谩mara:", error);
            return false;
        }
    };

    const stop = () => {
        if (animationFrameId !== null) {
            cancelAnimationFrame(animationFrameId);
            animationFrameId = null;
        }
        if (stream) {
            stream.getTracks().forEach(track => track.stop());
            stream = null;
            if (videoElement) {
                videoElement.srcObject = null;
            }
        }
        // Limpiar el canvas al detener
        if (canvasElement) {
            const canvasCtx = canvasElement.getContext('2d');
            if (canvasCtx) {
                 canvasCtx.clearRect(0, 0, canvasElement.width, canvasElement.height);
                 canvasCtx.fillStyle = '#4b5563'; // gris oscuro
                 canvasCtx.fillRect(0, 0, canvasElement.width, canvasElement.height);
                 canvasCtx.fillStyle = 'white';
                 canvasCtx.font = '24px Inter, sans-serif';
                 canvasCtx.textAlign = 'center';
                 canvasCtx.fillText('C谩mara detenida', canvasElement.width / 2, canvasElement.height / 2);
            }
        }
    };

    return { initialize, stop };
};
// =========================================================================

/**
 * Tipos y Constantes
 */
type Props = {
  modelId: string;
};

const RECOGNITION_INTERVAL_MS = 650;
const MIN_CONFIDENCE_THRESHOLD = 0.7;

interface CalculationState {
  num1: string;
  operator: string;
  num2: string;
  result: string;
  activeBlock: "num1" | "operator" | "num2" | "complete";
}

/**
 * Funci贸n auxiliar para evaluar expresiones de forma segura (sin `eval`)
 */
const safeEvaluateExpression = (expr: string): string => {
  // Solo permite n煤meros, operadores (+, -, *, /) y punto decimal.
  const sanitized = expr.replace(/[^0-9+\-*/.]/g, "");
  if (!sanitized) return "0";
  try {
    // eslint-disable-next-line no-new-func
    const val = Function(`"use strict"; return (${sanitized});`)();
    if (typeof val === "number" && isFinite(val)) return String(val);
    return "Error";
  } catch {
    return "Error";
  }
};

/**
 * Componente visual para los bloques de entrada
 */
const InputBlock: React.FC<{ title: string; value: string; isActive: boolean; dark?: boolean }> = ({ title, value, isActive, dark }) => {
  const base = "p-4 rounded-xl font-bold text-center border-2 transition-all duration-300 shadow-md";
  const bg = dark ? "bg-gray-800" : "bg-white";
  const border = isActive ? "border-indigo-400 ring-4 ring-indigo-500/30" : dark ? "border-gray-600" : "border-gray-300";
  const text = dark ? "text-white" : "text-gray-900";
  const titleColor = dark ? "text-gray-400" : "text-gray-600";

  return (
    <motion.div
      className={`${base} ${bg} ${border}`}
      animate={{ scale: isActive ? 1.03 : 1 }}
      transition={{ type: "spring", stiffness: 300, damping: 25 }}
    >
      <p className={`text-sm uppercase tracking-wider ${titleColor}`}>{title}</p>
      <div className={`text-3xl mt-1 ${text}`}>{value || (isActive ? "" : "")}</div>
    </motion.div>
  );
};

/**
 * Componente principal
 */
const SignLanguageCalculator: React.FC<Props> = ({ modelId }) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const mediaPipeHandler = useRef<MediaPipeHandler | null>(null); 

  const [cameraActive, setCameraActive] = useState(false);
  const [isInitializing, setIsInitializing] = useState(false);
  const [handsDetected, setHandsDetected] = useState(false);
  const [isAutoRecognizing, setIsAutoRecognizing] = useState(true);
  const [isPredicting, setIsPredicting] = useState(false);

  const [calcState, setCalcState] = useState<CalculationState>({
    num1: "",
    operator: "",
    num2: "",
    result: "",
    activeBlock: "num1",
  });

  const lastPredictionRef = useRef<string | null>(null);
  // Almacena solo los landmarks necesarios para la predicci贸n
  const currentLandmarksRef = useRef<Landmark[] | null>(null); 
  // Usa number | undefined, que es el tipo correcto para window.setInterval/clearInterval
  const recognitionIntervalRef = useRef<number | undefined>(undefined); 

  const isDarkMode = useMemo(() => document.body.classList.contains("dark"), []);

  /**
   * CORRECCIN 1: Manejo robusto de `null` para `multiHandLandmarks`
   * Callback que recibe los resultados simulados de MediaPipe.
   */
  const onResults = useCallback((results: Results) => {
    // 锔 Correcci贸n: Verificamos expl铆citamente si es null y si el array tiene elementos.
    const hasHands = !!(results.multiHandLandmarks && results.multiHandLandmarks.length > 0);
    setHandsDetected(hasHands);

    if (hasHands) {
      // Si hay manos, el array no ser谩 null. Usamos el primer array de landmarks.
      const rawLandmarks = results.multiHandLandmarks[0]; 
      
      // Asumimos que el modelo solo necesita las coordenadas x, y, z
      if (rawLandmarks && rawLandmarks.length > 0) {
        currentLandmarksRef.current = rawLandmarks.map(l => ({ x: l.x, y: l.y, z: l.z }));
      } else {
        currentLandmarksRef.current = null;
      }
    } else {
      currentLandmarksRef.current = null;
    }
  }, []);

  /**
   * Inicializa la c谩mara y el manejador de MediaPipe
   */
  const startCamera = useCallback(async () => {
    if (!videoRef.current || !canvasRef.current) {
      toast.error("No se encontr贸 video/canvas");
      return;
    }
    setIsInitializing(true);
    try {
      if (mediaPipeHandler.current) mediaPipeHandler.current.stop();
      mediaPipeHandler.current = createMediaPipeHandler();
      const ok = await mediaPipeHandler.current.initialize(videoRef.current, canvasRef.current, onResults);
      if (!ok) throw new Error("Error inicializando MediaPipe");
      setCameraActive(true);
      setIsAutoRecognizing(true);
      toast.success("C谩mara iniciada");
    } catch (err) {
      console.error(err);
      toast.error("No se pudo iniciar la c谩mara");
    } finally {
      setIsInitializing(false);
    }
  }, [onResults]);

  /**
   * CORRECCIN 2: Manejo de `undefined` para `clearInterval`
   * Detiene la c谩mara y limpia el intervalo de reconocimiento.
   */
  const stopCamera = useCallback(() => {
    if (mediaPipeHandler.current) mediaPipeHandler.current.stop();
    setCameraActive(false);
    setIsAutoRecognizing(false);
    setHandsDetected(false);
    currentLandmarksRef.current = null;
    lastPredictionRef.current = null;

    // 锔 Correcci贸n: Verificamos si la referencia tiene un valor antes de limpiar.
    if (recognitionIntervalRef.current !== undefined) {
      clearInterval(recognitionIntervalRef.current);
      recognitionIntervalRef.current = undefined; // Lo reiniciamos al estado inicial seguro
    }
    toast.success("C谩mara detenida");
  }, []);

  // reset calculator state
  const resetCalculator = useCallback(() => {
    setCalcState({ num1: "", operator: "", num2: "", result: "", activeBlock: "num1" });
    toast("Calculadora limpia", { icon: "Ч" });
  }, []);
  
  /**
   * Ejecuta la l贸gica de c谩lculo
   */
  const calculateResult = useCallback(() => {
    setCalcState(prev => {
      if (!prev.num1 || !prev.operator || !prev.num2) {
          toast.error("Aseg煤rate de tener un n煤mero, un operador y otro n煤mero.", { icon: "" });
          return prev;
      }

      const expression = `${prev.num1}${prev.operator}${prev.num2}`;
      const evalResult = safeEvaluateExpression(expression);

      if (evalResult === "Error") {
          toast.error("Expresi贸n inv谩lida", { icon: "锔" });
          return { ...prev, result: "Error" };
      }

      // C谩lculo exitoso: el resultado se convierte en el Num1 para encadenar
      toast.success(`Resultado: ${evalResult}`, { icon: "" });
      return { 
          num1: evalResult, 
          operator: '', 
          num2: '', 
          result: evalResult, 
          activeBlock: 'operator' // Esperando el siguiente operador
      };
    });
  }, []);


  /**
   * Procesa la predicci贸n de la se帽a en el estado de la calculadora
   */
  const processPrediction = useCallback((prediction: string) => {
    setCalcState((prev) => {
      // Limpiar (Clear)
      if (prediction === "clear") {
        return { num1: "", operator: "", num2: "", result: "", activeBlock: "num1" };
      }

      // Si hay un resultado y se detecta un n煤mero, iniciamos un nuevo c谩lculo
      if (prev.result !== '' && (!isNaN(Number(prediction)) || prediction === '.')) {
        return { num1: prediction, operator: '', num2: '', result: '', activeBlock: 'operator' };
      }


      // Num茅rico (incluye el punto decimal)
      if (!isNaN(Number(prediction)) || prediction === '.') {
        const char = prediction;
        if (prev.activeBlock === 'num1' || prev.activeBlock === 'operator') {
            if (char === '.' && prev.num1.includes('.')) return prev;
            // Permitir que el usuario siga escribiendo Num1 hasta que ponga un operador
            return { ...prev, num1: prev.num1 + char, activeBlock: 'operator' }; 
        }
        if (prev.activeBlock === 'num2' || prev.activeBlock === 'complete') {
            if (char === '.' && prev.num2.includes('.')) return prev;
            // Se marca como 'complete' cuando se ingresa el segundo n煤mero
            return { ...prev, num2: prev.num2 + char, activeBlock: 'complete' };
        }
        return prev;
      }

      // Operador
      if (["+", "-", "*", "/"].includes(prediction)) {
        if (prev.num1) {
            // Si ya hay un Num1, permitimos establecer el operador y pasar a Num2
            return { ...prev, operator: prediction, num2: "", activeBlock: "num2" };
        }
        return prev;
      }
      
      // La se帽a de "equal" ahora se ignora a favor del bot贸n
      if (prediction === "equal" && prev.num1 && prev.operator && prev.num2) {
          toast("Presiona 'Calcular Resultado' para completar.", { icon: "" });
      }

      return prev;
    });
  }, []);

  /**
   * Funci贸n para llamar a la API de predicci贸n (simulada)
   */
  const recognizeSign = useCallback(
    async (isAuto = false) => {
      if (!currentLandmarksRef.current) {
        if (!isAuto) toast.error("No se detecta la mano");
        return;
      }
      if (isPredicting) return;
      setIsPredicting(true);

      try {
        const payload = {
          modelId,
          landmarks: currentLandmarksRef.current,
        };

        const response = await api.post("/prediction/predict", payload);
        const { prediction, confidence } = response.data;

        if (confidence < MIN_CONFIDENCE_THRESHOLD) {
          if (!isAuto) toast.error("Se帽a no clara o baja confianza");
          lastPredictionRef.current = null;
          return;
        }

        // Evitar repetir la misma predicci贸n en modo autom谩tico
        if (isAuto && prediction === lastPredictionRef.current) {
          return;
        }
        lastPredictionRef.current = prediction;

        processPrediction(prediction);
      } catch (err) {
        console.error("Error en predict:", err);
        if (!isAuto) toast.error("Error comunicando con el modelo");
      } finally {
        setIsPredicting(false);
      }
    },
    [modelId, isPredicting, processPrediction]
  );

  /**
   * Efecto para el reconocimiento autom谩tico (Intervalo)
   */
  useEffect(() => {
    if (cameraActive && isAutoRecognizing) {
      // Limpiar cualquier intervalo existente
      if (recognitionIntervalRef.current !== undefined) {
        clearInterval(recognitionIntervalRef.current);
      }
      
      // Establecer el nuevo intervalo
      const id = window.setInterval(() => {
        if (currentLandmarksRef.current) recognizeSign(true);
      }, RECOGNITION_INTERVAL_MS);
      
      recognitionIntervalRef.current = id; 

      // Funci贸n de limpieza
      return () => {
        if (recognitionIntervalRef.current !== undefined) {
          clearInterval(recognitionIntervalRef.current);
          recognitionIntervalRef.current = undefined;
        }
      };
    } else {
      // Limpiar si se desactiva la c谩mara o el auto-reconocimiento
      if (recognitionIntervalRef.current !== undefined) {
        clearInterval(recognitionIntervalRef.current);
        recognitionIntervalRef.current = undefined;
      }
    }
  }, [cameraActive, isAutoRecognizing, recognizeSign]);

  // Limpieza al desmontar el componente
  useEffect(() => {
    return () => {
      stopCamera();
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  
  const isReadyToCalculate = !!(calcState.num1 && calcState.operator && calcState.num2);


  return (
    <div className="grid grid-cols-1 md:grid-cols-4 gap-8 p-4 font-['Inter'] min-h-screen bg-gray-50 dark:bg-gray-900 transition-colors duration-500">
      {/* SIDEBAR (Controles y Estado) */}
      <aside className="md:col-span-1 space-y-6">
        <div className={`rounded-2xl shadow-xl p-5 ${isDarkMode ? "bg-gray-800 border border-gray-700" : "bg-white border border-gray-200"}`}>
          <h3 className={`font-extrabold text-xl mb-4 ${isDarkMode ? "text-white" : "text-gray-900"}`}>Controles</h3>

          {/* Bot贸n Iniciar/Detener C谩mara */}
          <motion.button
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={cameraActive ? stopCamera : startCamera}
            disabled={isInitializing}
            className={`w-full px-4 py-3 rounded-xl font-bold text-white transition-all duration-300 flex items-center justify-center gap-2 shadow-lg ${
              isInitializing ? "bg-gray-500 cursor-not-allowed" : cameraActive ? "bg-red-600 hover:bg-red-700" : "bg-blue-600 hover:bg-blue-700"
            }`}
          >
            {isInitializing ? <RefreshCw className="animate-spin w-5 h-5" /> : <Power className="w-5 h-5" />}
            {isInitializing ? "Inicializando..." : cameraActive ? "Detener C谩mara" : "Iniciar C谩mara"}
          </motion.button>

          {/* Bot贸n de Auto-Reconocimiento */}
          {cameraActive && (
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={() => setIsAutoRecognizing((p) => !p)}
              className={`mt-3 w-full px-4 py-3 rounded-xl font-bold text-white transition-all duration-300 flex items-center justify-center gap-2 shadow-lg ${
                isAutoRecognizing ? "bg-purple-600 hover:bg-purple-700" : "bg-yellow-600 hover:bg-yellow-700"
              }`}
            >
              <Zap className={`w-5 h-5 ${isAutoRecognizing ? "animate-pulse" : ""}`} />
              {isAutoRecognizing ? "Auto ON" : "Auto OFF"}
            </motion.button>
          )}

          {/* Bot贸n de Reconocimiento Manual */}
          {cameraActive && !isAutoRecognizing && (
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={() => recognizeSign(false)}
              disabled={isPredicting || !handsDetected}
              className={`mt-3 w-full px-4 py-3 rounded-xl font-bold text-white transition-all duration-300 flex items-center justify-center gap-2 ${
                isPredicting ? "bg-indigo-400 cursor-wait" : "bg-indigo-600 hover:bg-indigo-700"
              } disabled:opacity-50`}
            >
              {isPredicting ? <RefreshCw className="animate-spin w-5 h-5" /> : ""}
              Reconocer (Manual)
            </motion.button>
          )}

          {/* Bot贸n de Limpiar Calculadora */}
          {(calcState.num1 || calcState.operator || calcState.num2 || calcState.result) && (
            <motion.button
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              onClick={resetCalculator}
              className="mt-3 w-full px-4 py-3 rounded-xl font-bold text-white transition-all duration-300 flex items-center justify-center gap-2 bg-pink-600 hover:bg-pink-700 shadow-lg"
            >
              <RefreshCw className="w-5 h-5" />
              Limpiar Todo
            </motion.button>
          )}
        </div>

        {/* Estado del Sistema */}
        <div className={`rounded-2xl shadow-xl p-5 ${isDarkMode ? "bg-gray-800 border border-gray-700" : "bg-white border border-gray-200"}`}>
          <h3 className={`font-extrabold text-xl mb-2 ${isDarkMode ? "text-white" : "text-gray-900"}`}>Estado del Sistema</h3>
          <p className="flex items-center gap-2 font-medium">
            {handsDetected ? (
              <span className="text-green-500 flex items-center gap-2">
                <CheckCircle className="w-5 h-5" /> Mano detectada
              </span>
            ) : (
              <span className="text-red-500 flex items-center gap-2">
                <XCircle className="w-5 h-5" /> Esperando mano
              </span>
            )}
          </p>
          {isAutoRecognizing && (
            <p className="flex items-center gap-2 mt-2 text-purple-500 font-medium">
              <Zap className="w-5 h-5" /> Reconocimiento Autom谩tico Activo
            </p>
          )}
        </div>
      </aside>

      {/* MAIN (C谩mara y Calculadora) */}
      <section className="md:col-span-3 space-y-6">
        <motion.h2 initial={{ opacity: 0, y: 10 }} animate={{ opacity: 1, y: 0 }} className={`text-3xl font-extrabold text-center ${isDarkMode ? "text-blue-300" : "text-gray-800"}`}>
          Calculadora con Lenguaje de Se帽as
        </motion.h2>

        {/* rea de Video/Canvas */}
        <div className="relative bg-black rounded-2xl overflow-hidden shadow-2xl mx-auto max-w-2xl aspect-video">
          <video ref={videoRef as any} className="hidden" autoPlay muted playsInline />
          <canvas ref={canvasRef as any} width={640} height={480} className="w-full h-full object-cover" />
          {!cameraActive && (
            <div className="absolute inset-0 flex flex-col items-center justify-center bg-gray-900/90 text-white p-4">
              <Power className="w-10 h-10 mb-3 text-red-400" />
              <p className="text-lg font-medium">Pulsa "Iniciar C谩mara" para comenzar.</p>
              {isInitializing && <p className="mt-2 text-sm text-gray-400 flex items-center gap-2"><RefreshCw className="w-5 h-5 animate-spin" /> Cargando...</p>}
            </div>
          )}
        </div>

        {/* Interfaz de la Calculadora */}
        <div className="max-w-2xl mx-auto p-4 rounded-2xl shadow-2xl space-y-4">
          <div className="grid grid-cols-5 gap-4 items-center">
            <div className="col-span-2">
              <InputBlock title="NMERO 1" value={calcState.num1} isActive={calcState.activeBlock === "num1" || calcState.activeBlock === "operator"} dark={isDarkMode} />
            </div>
            <div className="col-span-1">
              <InputBlock title="OPERADOR" value={calcState.operator} isActive={calcState.activeBlock === "operator" || calcState.activeBlock === "num2"} dark={isDarkMode} />
            </div>
            <div className="col-span-2">
              <InputBlock title="NMERO 2" value={calcState.num2} isActive={calcState.activeBlock === "num2" || calcState.activeBlock === "complete"} dark={isDarkMode} />
            </div>
          </div>

          {/* BOTN DE CALCULAR RESULTADO */}
          <motion.button
              onClick={calculateResult}
              disabled={!isReadyToCalculate}
              whileHover={{ scale: isReadyToCalculate ? 1.01 : 1 }}
              whileTap={{ scale: isReadyToCalculate ? 0.99 : 1 }}
              className={`w-full py-4 rounded-xl font-bold text-lg transition-all duration-300 shadow-xl flex items-center justify-center gap-3 mt-4 ${
                  isReadyToCalculate 
                      ? "bg-green-600 hover:bg-green-700 text-white" 
                      : "bg-gray-400 text-gray-700 cursor-not-allowed"
              }`}
          >
              <Equal className="w-6 h-6" />
              CALCULAR RESULTADO
          </motion.button>

          {/* Display de Resultado */}
          <div className={`w-full p-6 rounded-2xl shadow-inner transition-colors duration-300 ${isDarkMode ? "bg-gray-700 border-2 border-indigo-600" : "bg-white border-2 border-indigo-400"}`}>
            <p className={`text-right text-sm uppercase font-semibold ${isDarkMode ? "text-indigo-400" : "text-indigo-600"}`}>Resultado</p>
            <div className={`text-right text-6xl font-extrabold mt-1 transition-colors duration-300 ${isDarkMode ? "text-white" : "text-gray-900"}`}>
              <motion.span key={calcState.result} initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ duration: 0.3 }}>
                {calcState.result || "0"}
              </motion.span>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default SignLanguageCalculator;
