import React, { useState, useRef, useEffect, useCallback, useMemo } from "react";
import { motion } from "framer-motion";
import {
  XCircle,
  CheckCircle,
  RefreshCw,
  Power,
  Zap,
} from "lucide-react"; // Iconos para la UI
import toast from "react-hot-toast";
import { Results } from "@mediapipe/hands"; // Importaci√≥n de tipo simulada

// --- 1. Definici√≥n de la Utilidad API (Reemplaza ../utils/api) ---
/**
 * Funci√≥n simulada para llamar al endpoint de predicci√≥n.
 * Utiliza fetch para realizar la llamada POST.
 */
const api = {
  post: async (path: string, data: any) => {
    const apiKey = ""; // La clave API se inyecta por el entorno
    const predictionApiUrl = `https://generativelanguage.googleapis.com/v1beta/models/predict:predict?key=${apiKey}`; 
    
    const payload = {
        modelId: data.modelId,
        instances: [{
            landmarks: data.landmarks
        }]
    };

    const response = await fetch(predictionApiUrl, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(payload)
    });

    // Simulaci√≥n de una respuesta exitosa, ya que el servicio real no est√° disponible.
    if (response.ok) {
        // Simulaci√≥n de datos para demostraci√≥n.
        // Simulamos que el modelo puede devolver n√∫meros (0-9), operadores (+,-,*,/), 'equal' y 'clear'.
        const mockPredictions = ["1", "2", "3", "4", "+", "*", "equal", "clear"];
        const randomPrediction = mockPredictions[Math.floor(Math.random() * mockPredictions.length)];

        return { 
            data: { 
                prediction: randomPrediction, 
                confidence: 0.95 
            }
        };
    } else {
        throw new Error(`API call failed with status: ${response.status}`);
    }
  }
};


// --- 2. Definici√≥n de la Utilidad MediaPipe (Reemplaza ../utils/mediapipe) ---

// Tipo de utilidad simple para MediaPipe (simplificada para el entorno de archivo √∫nico)
interface MediaPipeHandler {
    initialize: (videoElement: HTMLVideoElement, canvasElement: HTMLCanvasElement, onResults: (results: Results) => void) => Promise<boolean>;
    stop: () => void;
}

// Implementaci√≥n simulada de createMediaPipeHandler
const createMediaPipeHandler = (): MediaPipeHandler => {
    let stream: MediaStream | null = null;
    let animationFrameId: number | null = null;
    let videoElement: HTMLVideoElement | null = null;
    let canvasElement: HTMLCanvasElement | null = null;
    let onResultsCallback: ((results: Results) => void) | null = null;

    const runDetectionLoop = () => {
        if (!videoElement || !canvasElement || !onResultsCallback) return;

        // 1. Detecci√≥n (SIMULADA)
        const mockResults: Results = {
            multiHandLandmarks: stream ? [
                // Simulaci√≥n de 21 puntos de referencia cuando la c√°mara est√° activa
                Array.from({ length: 21 }, (_, i) => ({
                    x: Math.random(),
                    y: Math.random(),
                    z: Math.random(),
                    visibility: 1
                }))
            ] : [],
            image: canvasElement, // Simulaci√≥n de la imagen para dibujar
            multiHandedness: stream ? [{ label: 'Right', score: 0.9 }] : [],
        } as unknown as Results; 

        onResultsCallback(mockResults);

        // 2. Dibujo en Canvas (Simplificado)
        const canvasCtx = canvasElement.getContext('2d');
        if (canvasCtx) {
            canvasCtx.save();
            canvasCtx.clearRect(0, 0, canvasElement.width, canvasElement.height);

            if (videoElement.readyState === videoElement.HAVE_ENOUGH_DATA) {
                // Dibujar el video
                canvasCtx.drawImage(videoElement, 0, 0, canvasElement.width, canvasElement.height);

                // Indicador de mano simulada
                if (mockResults.multiHandLandmarks && mockResults.multiHandLandmarks.length > 0) {
                    canvasCtx.fillStyle = 'rgba(74, 222, 128, 0.5)'; // Verde de Tailwind 400
                    canvasCtx.beginPath();
                    canvasCtx.arc(canvasElement.width / 2, canvasElement.height / 2, 20, 0, 2 * Math.PI);
                    canvasCtx.fill();
                }
            } else {
                 // Texto de "Cargando" si el video no est√° listo
                canvasCtx.fillStyle = 'white';
                canvasCtx.fillRect(0, 0, canvasElement.width, canvasElement.height);
                canvasCtx.fillStyle = 'black';
                canvasCtx.font = '24px sans-serif';
                canvasCtx.textAlign = 'center';
                canvasCtx.fillText('C√°mara activa, cargando video...', canvasElement.width / 2, canvasElement.height / 2);
            }
            canvasCtx.restore();
        }

        animationFrameId = requestAnimationFrame(runDetectionLoop);
    };

    const initialize = async (video: HTMLVideoElement, canvas: HTMLCanvasElement, onResults: (results: Results) => void) => {
        videoElement = video;
        canvasElement = canvas;
        onResultsCallback = onResults;

        try {
            stream = await navigator.mediaDevices.getUserMedia({ video: true });
            video.srcObject = stream;
            
            await new Promise((resolve) => {
                video.onloadedmetadata = () => {
                    video.play();
                    resolve(true);
                };
            });

            runDetectionLoop();
            return true;
        } catch (error) {
            console.error("Error al acceder a la c√°mara:", error);
            return false;
        }
    };

    const stop = () => {
        if (animationFrameId !== null) {
            cancelAnimationFrame(animationFrameId);
            animationFrameId = null;
        }
        if (stream) {
            stream.getTracks().forEach(track => track.stop());
            stream = null;
            if (videoElement) {
                videoElement.srcObject = null;
            }
        }
         // Limpiar canvas al detener
        if (canvasElement) {
            const canvasCtx = canvasElement.getContext('2d');
            canvasCtx?.clearRect(0, 0, canvasElement.width, canvasElement.height);
        }
    };

    return { initialize, stop };
};

// Fin de la definici√≥n de la Utilidad MediaPipe
// -------------------------------------------------------------------


// ‚úÖ Props para recibir el ID del modelo
type Props = {
  modelId: string;
};

// --- Constantes de la Calculadora ---
const RECOGNITION_INTERVAL_MS = 600; // Frecuencia de reconocimiento autom√°tico
const MIN_CONFIDENCE_THRESHOLD = 0.7; // Umbral m√≠nimo de confianza para aceptar una se√±a


// --- Implementaci√≥n Segura para la Evaluaci√≥n de Expresiones ---
const safeEvaluateExpression = (expr: string): string => {
  const sanitizedExpr = expr.replace(/[^0-9\s\.\+\-\*\/]/g, '');

  if (sanitizedExpr.length === 0) {
      return "0";
  }

  try {
    const result = new Function('return ' + sanitizedExpr)();
    
    if (typeof result === 'number' && isFinite(result)) {
        return result.toString();
    }
    throw new Error('Invalid calculation result');
  } catch (e) {
    console.error("Safe evaluation error:", e);
    return "Error";
  }
};


// --- Nuevo Tipo de Estado para la Calculadora de 3 Bloques ---
interface CalculationState {
    num1: string;
    operator: string;
    num2: string;
    result: string;
    activeBlock: 'num1' | 'operator' | 'num2' | 'complete'; // Define qu√© bloque est√° listo para recibir la siguiente se√±a
}

const SignLanguageCalculator: React.FC<Props> = ({ modelId }) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const mediaPipeHandler = useRef<MediaPipeHandler | null>(null);

  const [cameraActive, setCameraActive] = useState(false);
  const [isInitializing, setIsInitializing] = useState(false);
  const [handsDetected, setHandsDetected] = useState(false);
  const [isAutoRecognizing, setIsAutoRecognizing] = useState(true); // Auto-reconocimiento por defecto

  const [isPredicting, setIsPredicting] = useState(false); // Estado de carga de la predicci√≥n

  // Estado que contiene los tres bloques de la nueva interfaz
  const [calcState, setCalcState] = useState<CalculationState>({
      num1: '',
      operator: '',
      num2: '',
      result: '',
      activeBlock: 'num1',
  });
  
  const lastPredictionRef = useRef<string | null>(null); 
  const currentLandmarksRef = useRef<any[] | null>(null); 
  const recognitionIntervalRef = useRef<number | null>(null);

  const isDarkMode = useMemo(() => document.body.classList.contains('dark'), []);

  const onResults = useCallback((results: any): void => { 
    const hasHands =
      results.multiHandLandmarks && results.multiHandLandmarks.length > 0;
    setHandsDetected(hasHands);

    if (hasHands && results.multiHandLandmarks[0] && results.multiHandLandmarks[0].length === 21) {
      currentLandmarksRef.current = results.multiHandLandmarks[0];
    } else {
      currentLandmarksRef.current = null;
    }
  }, []);

  // Funci√≥n para resetear el estado y comenzar una nueva operaci√≥n
  const resetCalculator = useCallback(() => {
    setCalcState({ num1: '', operator: '', num2: '', result: '', activeBlock: 'num1' });
  }, []);


  const startCamera = useCallback(async () => {
    if (!videoRef.current || !canvasRef.current) {
      toast.error("Error: No se encontr√≥ el video/canvas");
      return;
    }
    setIsInitializing(true);
    try {
      if (mediaPipeHandler.current) {
        mediaPipeHandler.current.stop();
      }

      mediaPipeHandler.current = createMediaPipeHandler();
      const success = await mediaPipeHandler.current.initialize(
        videoRef.current,
        canvasRef.current,
        onResults
      );

      if (success) {
        setCameraActive(true);
        setIsAutoRecognizing(true); 
        toast.success("C√°mara y reconocimiento iniciados", { icon: "üé•" });
      }
    } catch (err) {
      console.error(err);
      toast.error("Error al iniciar la c√°mara");
    } finally {
      setIsInitializing(false);
    }
  }, [onResults]);

  const stopCamera = useCallback(() => {
    if (mediaPipeHandler.current) {
      mediaPipeHandler.current.stop();
      setCameraActive(false);
      setIsAutoRecognizing(false);
      setHandsDetected(false);
      currentLandmarksRef.current = null;
      lastPredictionRef.current = null;
      if (recognitionIntervalRef.current !== null) {
        clearInterval(recognitionIntervalRef.current);
      }
      toast.success("C√°mara y reconocimiento detenidos", { icon: "üõë" });
    }
  }, []);

  // Funci√≥n para procesar la se√±a detectada y actualizar el estado de la calculadora
  const processPrediction = useCallback((prediction: string) => {
    setCalcState(prev => {
        // Si hay un resultado previo, "clear" impl√≠cito para comenzar
        if (prev.result !== '' && prediction !== 'clear') {
            const newState = { num1: '', operator: '', num2: '', result: '', activeBlock: 'num1' } as CalculationState;
            // Si el siguiente es un n√∫mero, lo ponemos en num1 (permite encadenar: ej. 5 + 3 = 8, luego se√±a 2 -> 2 + ...)
            if (!isNaN(Number(prediction))) {
                return { ...newState, num1: prediction, activeBlock: 'operator' };
            }
            // Si es un operador o cualquier otra cosa, simplemente reseteamos
            return newState;
        }

        // L√≥gica de borrado
        if (prediction === "clear") {
            return { num1: '', operator: '', num2: '', result: '', activeBlock: 'num1' };
        }

        // Predicciones de n√∫meros (0-9)
        if (!isNaN(Number(prediction))) {
            const num = prediction;
            if (prev.activeBlock === 'num1') {
                return { ...prev, num1: prev.num1 + num, activeBlock: 'operator' };
            }
            if (prev.activeBlock === 'num2') {
                return { ...prev, num2: prev.num2 + num, activeBlock: 'complete' };
            }
            return prev; // Ignorar n√∫meros si se espera operador o si ya est√° completa
        }

        // Predicciones de operadores (+, -, *, /)
        if (["+", "-", "*", "/"].includes(prediction)) {
            if (prev.activeBlock === 'operator' && prev.num1 !== '') {
                // Si ya hay un operador, lo reemplazamos
                return { ...prev, operator: prediction, activeBlock: 'num2' };
            }
            return prev; // Ignorar si num1 est√° vac√≠o o si se espera num2
        }

        // Predicci√≥n de Igual (=)
        if (prediction === "equal") {
            // Activar c√°lculo solo si todos los campos requeridos est√°n presentes
            if (prev.num1 && prev.operator && prev.num2) {
                const expression = `${prev.num1}${prev.operator}${prev.num2}`;
                const evalResult = safeEvaluateExpression(expression);
                
                if (evalResult === "Error") {
                    toast.error("Expresi√≥n inv√°lida");
                    return { ...prev, result: "Error" };
                }

                // C√°lculo exitoso: movemos el resultado a num1 y limpiamos para encadenar
                return { 
                    num1: evalResult, 
                    operator: '', 
                    num2: '', 
                    result: evalResult, 
                    activeBlock: 'operator' 
                };
            } else {
                toast.error("Faltan n√∫meros u operador para calcular (Ej: Num1 + Num2)");
                return prev;
            }
        }

        // Ignorar cualquier otra se√±a no definida
        return prev;
    });
  }, []);

  // Funci√≥n central para predecir la se√±a
  const recognizeSign = useCallback(async (isAuto: boolean = false) => {
    if (!currentLandmarksRef.current) {
      if (!isAuto) toast.error("No se detecta la mano");
      return;
    }

    if (isPredicting) return;
    setIsPredicting(true);

    try {
      const landmarksPayload = currentLandmarksRef.current.map((l: any) => ({
        x: l.x,
        y: l.y,
        z: l.z,
      }));

      const response = await api.post("/prediction/predict", {
        modelId,
        landmarks: landmarksPayload,
      });

      const { prediction, confidence } = response.data;

      // 1. Manejo de baja confianza
      if (confidence < MIN_CONFIDENCE_THRESHOLD) {
        if (!isAuto) toast.error("Se√±a no clara, intenta de nuevo");
        lastPredictionRef.current = null; 
        return;
      }
      
      // 2. Control de duplicados en modo autom√°tico
      if (isAuto && prediction === lastPredictionRef.current) {
          return;
      }
      lastPredictionRef.current = prediction;

      // 3. Procesamiento de la se√±a
      processPrediction(prediction);
      
    } catch (err) {
      console.error("Error predicting sign:", err);
      if (!isAuto) toast.error("Error al comunicarse con el modelo");
    } finally {
      setIsPredicting(false);
    }
  }, [modelId, isPredicting, processPrediction]);


  // EFECTO para el Reconocimiento Autom√°tico
  useEffect(() => {
    if (cameraActive && isAutoRecognizing) {
        if (recognitionIntervalRef.current !== null) {
            clearInterval(recognitionIntervalRef.current);
        }

        const intervalId = setInterval(() => {
            if (currentLandmarksRef.current) {
                recognizeSign(true); 
            }
        }, RECOGNITION_INTERVAL_MS);

        recognitionIntervalRef.current = intervalId as unknown as number;

        return () => {
            if (recognitionIntervalRef.current !== null) {
                clearInterval(recognitionIntervalRef.current);
                recognitionIntervalRef.current = null;
            }
        };
    } else if (recognitionIntervalRef.current !== null) {
        clearInterval(recognitionIntervalRef.current);
        recognitionIntervalRef.current = null;
    }
  }, [cameraActive, isAutoRecognizing, recognizeSign]);

  // Limpieza al desmontar el componente
  useEffect(() => {
      return () => {
          stopCamera();
      }
  }, [stopCamera]);

  // Componente de Bloque de Entrada para la UI
  const InputBlock = ({ title, value, isActive }: { title: string; value: string; isActive: boolean }) => {
    const defaultClasses = "p-4 rounded-xl font-bold text-center border-2 transition-all duration-300 shadow-md";
    
    // Clases din√°micas basadas en el tema y el estado
    const background = isDarkMode ? 'bg-gray-700' : 'bg-white';
    const border = isActive 
        ? 'border-indigo-400 ring-4 ring-indigo-500/50' 
        : (isDarkMode ? 'border-gray-600' : 'border-gray-300');
    const valueColor = isDarkMode ? 'text-white' : 'text-gray-900';
    const titleColor = isDarkMode ? 'text-gray-400' : 'text-gray-600';

    return (
        <motion.div
            className={`${defaultClasses} ${background} ${border}`}
            animate={{ scale: isActive ? 1.05 : 1 }}
            transition={{ type: "spring", stiffness: 300, damping: 20 }}
        >
            <p className={`text-sm uppercase tracking-wider ${titleColor}`}>{title}</p>
            <div className={`text-3xl mt-1 ${valueColor}`}>
                {value || (isActive ? '‚úã' : '‚Äî')}
            </div>
        </motion.div>
    );
  };


  return (
    <div className="grid grid-cols-1 md:grid-cols-4 gap-8 p-4">
      
      {/* Sidebar - Controles */}
      <aside className="md:col-span-1 space-y-6">
        <div className={`rounded-2xl shadow-xl p-5 ${isDarkMode ? 'bg-gray-800 border border-gray-700' : 'bg-white border border-gray-200'}`}>
          <h3 className={`font-extrabold text-xl mb-4 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
            Controles 
          </h3>
          
          {/* Bot√≥n Iniciar/Detener C√°mara */}
          <motion.button
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={cameraActive ? stopCamera : startCamera}
            disabled={isInitializing}
            className={`w-full px-4 py-3 rounded-xl font-bold text-white transition-all duration-300 flex items-center justify-center gap-2 shadow-lg ${
              isInitializing ? "bg-gray-500 cursor-not-allowed" : 
              cameraActive ? "bg-red-600 hover:bg-red-700" : "bg-blue-600 hover:bg-blue-700"
            }`}
          >
            {isInitializing ? (
              <RefreshCw className="animate-spin w-5 h-5" />
            ) : cameraActive ? (
              <Power className="w-5 h-5" />
            ) : (
              <Power className="w-5 h-5" />
            )}
            {isInitializing ? "Inicializando..." : cameraActive ? "Detener C√°mara" : "Iniciar C√°mara"}
          </motion.button>

          {/* Bot√≥n de Reconocimiento Autom√°tico (Toggle) */}
          {cameraActive && (
              <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={() => setIsAutoRecognizing(prev => !prev)}
                  className={`mt-3 w-full px-4 py-3 rounded-xl font-bold text-white transition-all duration-300 flex items-center justify-center gap-2 shadow-lg ${
                      isAutoRecognizing ? "bg-purple-600 hover:bg-purple-700" : "bg-yellow-600 hover:bg-yellow-700"
                  }`}
              >
                  <Zap className={`w-5 h-5 ${isAutoRecognizing ? 'animate-pulse' : ''}`} />
                  {isAutoRecognizing ? "Reconocimiento Autom√°tico ON" : "Activar Reconocimiento"}
              </motion.button>
          )}

          {/* Bot√≥n de Reconocimiento Manual (solo visible si el auto-rec est√° apagado) */}
          {cameraActive && !isAutoRecognizing && (
              <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={() => recognizeSign(false)}
                  disabled={isPredicting || !handsDetected}
                  className={`mt-3 w-full px-4 py-3 rounded-xl font-bold text-white transition-all duration-300 flex items-center justify-center gap-2 ${
                    isPredicting ? "bg-indigo-400 cursor-wait" : "bg-indigo-600 hover:bg-indigo-700"
                  } disabled:opacity-50`}
              >
                  {isPredicting ? <RefreshCw className="animate-spin w-5 h-5" /> : '‚úã'}
                  Reconocer Se√±a (Manual)
              </motion.button>
          )}

          {/* Bot√≥n de Limpiar */}
          {(calcState.num1 || calcState.operator || calcState.num2) && (
             <motion.button
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  onClick={resetCalculator}
                  className="mt-3 w-full px-4 py-3 rounded-xl font-bold text-white transition-all duration-300 flex items-center justify-center gap-2 bg-pink-600 hover:bg-pink-700 shadow-lg"
              >
                  <RefreshCw className="w-5 h-5" />
                  Limpiar Calculadora
              </motion.button>
          )}

        </div>

        {/* Estado de la Detecci√≥n */}
        <div className={`rounded-2xl shadow-xl p-5 ${isDarkMode ? 'bg-gray-800 border border-gray-700' : 'bg-white border border-gray-200'}`}>
          <h3 className={`font-extrabold text-xl mb-2 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
            Estado del Sistema
          </h3>
          <p className="flex items-center gap-2 font-medium">
            {handsDetected ? (
              <span className="text-green-500 flex items-center gap-2">
                <CheckCircle className="w-5 h-5" /> Mano detectada
              </span>
            ) : (
              <span className="text-red-500 flex items-center gap-2">
                <XCircle className="w-5 h-5" /> Esperando mano
              </span>
            )}
          </p>
          {isAutoRecognizing && (
              <p className="flex items-center gap-2 mt-2 text-purple-500 font-medium">
                <Zap className="w-5 h-5" /> Reconocimiento Autom√°tico Activo
              </p>
          )}
        </div>
      </aside>

      {/* Main Area - Video y Pantalla de la Calculadora */}
      <section className="md:col-span-3 space-y-6">
        <motion.h2
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          className={`text-3xl font-extrabold text-center ${isDarkMode ? 'text-blue-300' : 'text-gray-800'}`}
        >
          Calculadora con Lenguaje de Se√±as
        </motion.h2>

        {/* Contenedor de Video/Canvas */}
        <div className="relative bg-black rounded-2xl overflow-hidden shadow-2xl mx-auto max-w-2xl aspect-video">
          <video ref={videoRef} className="hidden" autoPlay muted playsInline />
          <canvas
            ref={canvasRef}
            width={640}
            height={480}
            className="w-full h-full object-cover"
          />

          {!cameraActive && (
            <div className="absolute inset-0 flex flex-col items-center justify-center bg-gray-900/90 text-white p-4">
              <Power className="w-10 h-10 mb-3 text-red-400" />
              <p className="text-lg font-medium">Pulsa "Iniciar C√°mara" para comenzar.</p>
              {isInitializing && <p className="mt-2 text-sm text-gray-400 flex items-center gap-2"><RefreshCw className="w-5 h-5 animate-spin" /> Cargando MediaPipe...</p>}
            </div>
          )}
        </div>

        {/* Bloques de Entrada de la Calculadora (3 Bloques) */}
        <div 
            className="max-w-2xl mx-auto p-4 rounded-2xl shadow-2xl space-y-6"
        >
            <div className="grid grid-cols-5 gap-4 items-center">
                {/* Bloque N√∫mero 1 */}
                <div className="col-span-2">
                    <InputBlock 
                        title="N√öMERO 1" 
                        value={calcState.num1} 
                        isActive={calcState.activeBlock === 'num1'} 
                    />
                </div>

                {/* Bloque Operador */}
                <div className="col-span-1">
                    <InputBlock 
                        title="SE√ëA" 
                        value={calcState.operator} 
                        isActive={calcState.activeBlock === 'operator'} 
                    />
                </div>

                {/* Bloque N√∫mero 2 */}
                <div className="col-span-2">
                    <InputBlock 
                        title="N√öMERO 2" 
                        value={calcState.num2} 
                        isActive={calcState.activeBlock === 'num2'} 
                    />
                </div>
            </div>

            {/* Pantalla del Resultado */}
            <div className={`w-full p-6 rounded-2xl shadow-inner transition-colors duration-300 ${isDarkMode ? 'bg-gray-800 border-2 border-indigo-600' : 'bg-white border-2 border-indigo-400'}`}>
                <p className={`text-right text-sm uppercase font-semibold ${isDarkMode ? 'text-indigo-400' : 'text-indigo-600'}`}>
                    Resultado
                </p>
                <div className={`text-right text-6xl font-extrabold mt-1 transition-colors duration-300 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                    <motion.span
                        key={calcState.result} 
                        initial={{ y: 20, opacity: 0 }}
                        animate={{ y: 0, opacity: 1 }}
                        transition={{ duration: 0.3 }}
                    >
                        {calcState.result || '0'}
                    </motion.span>
                </div>
            </div>
        </div>
      </section>
    </div>
  );
};

export default SignLanguageCalculator;
